function flipCoin() {
    let headsCount = 0;
    let tailsCount = 0;

    while (headsCount < 11 && tailsCount < 11) {
      if (Math.random() < 0.5) {
        headsCount++;
        console.log("Heads");
      } else {
        tailsCount++;
        console.log("Tails");
      }
    }
  
    console.log("Heads count:", headsCount);
    console.log("Tails count:", tailsCount);
  }
  
  flipCoin();
  